package cn.digione.yibaic.shop.acitvity;import android.os.Bundle;import android.support.v4.app.Fragment;import cn.digione.yibaic.shop.R;import cn.digione.yibaic.shop.ui.LoginFragment;/** * Created with IntelliJ IDEA. User: youzh Date: 14-3-4 Time: 上午10:46 */public class LoginActivity extends BaseActivity {	@Override	protected Fragment newFragmentByTag(String s) {		Fragment fragment = null;		if ("login_fragment".equals(s)) {			fragment = new LoginFragment();		}		return fragment;	}	@Override	protected void onCreate(Bundle bundle) {		super.onCreate(bundle);		setCustomContentView(R.layout.login_activity);		setTitle(R.string.title_login);		showFragment("login_fragment", getIntent().getExtras(), false);		setShoppingBarEnable(false);	}}